class PoliticalGroups:
    def __init__(self, political_affiliations, political_activism, voting_behavior):
        self.political_affiliations = political_affiliations  # Политическая принадлежность, например, 'демократ', 'республиканец', 'независимый'
        self.political_activism = political_activism  # Уровень политической активности, например, 'активист', 'обычный гражданин'
        self.voting_behavior = voting_behavior  # Поведение при голосовании, например, 'голосует всегда', 'голосует иногда', 'не голосует'